mapper.insert:
/* FileDAO.insertContents */
INSERT INTO T_ATCH_FILE (
      ATCH_FILE_ID
    , REG_ID
    , FILE_SAVE_PATH
    , ORG_FILE_NM
    , SAVE_FILE_NM
    , FILE_TYPE
    , FILE_SIZE
) VALUES (
      #{atchFileId}
    , #{loginId}
    , #{fileSavePath}
    , #{orgFileNm}
    , #{saveFileNm}
    , #{fileType}
    , #{fileSize}
)

mapper.delete:
/* FileDAO.deleteContents */
UPDATE T_ATCH_FILE
SET DEL_YN = 'Y'
  , MOD_DT = SYSDATE
  , MOD_ID = #{loginId}
WHERE ATCH_FILE_ID = #{atchFileId}
AND DEL_YN = 'N'
