mapper.insert:
/* MaEventDAO.insertContents */
INSERT INTO T_EVENT (
      SEQ
    , REG_ID
    , STRT_DT
    , END_DT
    , ALERT_TIME
    , CONT
) VALUES (
      T_EVENT_SEQ.NEXTVAL
    , #{loginId}
    , #{strtDt}
    , #{endDt}
    , #{alertTime}
    , #{cont}
)

mapper.update:
/* MaEventDAO.updateContents */
UPDATE T_EVENT
SET MOD_ID = #{loginId}
  , MOD_DT = SYSDATE
  , STRT_DT = #{strtDt}
  , END_DT = #{endDt}
  , ALERT_TIME = #{alertTime}
  , CONT = #{cont}
WHERE SEQ = #{seq}
AND DEL_YN = 'N'

mapper.delete:
/* MaEventDAO.deleteContents */
UPDATE T_EVENT
SET DEL_YN = 'Y'
  , MOD_DT = SYSDATE
  , MOD_ID = #{loginId}
WHERE SEQ = #{seq}
