mapper.select:
/* MaUserDAO.countByUserId */
SELECT COUNT(1)
FROM T_USER
WHERE USER_ID = #{userId}
AND SEQ != #{seq}
AND DEL_YN = 'N'

mapper.insert:
/* MaUserDAO.insertContents */
INSERT INTO T_USER (
      SEQ
    , REG_ID
    , USER_ID
    , USER_PW
    , USER_NM
    , USER_EMAIL
    , USER_TEL
    , USER_POST
    , USER_ADDR
    , USER_ADDR_DETAIL
    , MRK_YN
    , AUTH
) VALUES (
      T_USER_SEQ.NEXTVAL
    , #{loginId}
    , #{userId}
    , #{userPw}
    , #{userNm}
    , #{userEmail}
    , #{userTel}
    , #{userPost}
    , #{userAddr}
    , #{userAddrDetail}
    , #{mrkYn}
    , #{auth}
)

mapper.update:
/* MaUserDAO.updateContents */
UPDATE T_USER
SET MOD_ID = #{loginId}
  , MOD_DT = SYSDATE
  , USER_ID = #{userId}
  , USER_NM = #{userNm}
  , USER_EMAIL = #{userEmail}
  , USER_TEL = #{userTel}
  , USER_POST = #{userPost}
  , USER_ADDR = #{userAddr}
  , USER_ADDR_DETAIL = #{userAddrDetail}
  , MRK_YN = #{mrkYn}
  , AUTH = #{auth}
WHERE SEQ = #{seq}
AND DEL_YN = 'N'

/* MaUserDAO.passwordUpdateContents */
UPDATE T_USER
SET MOD_ID = #{loginId}
  , MOD_DT = SYSDATE
  , USER_ID = #{userId}
  , USER_PW = #{userPw}
  , USER_NM = #{userNm}
  , USER_EMAIL = #{userEmail}
  , USER_TEL = #{userTel}
  , USER_POST = #{userPost}
  , USER_ADDR = #{userAddr}
  , USER_ADDR_DETAIL = #{userAddrDetail}
  , MRK_YN = #{mrkYn}
  , AUTH = #{auth}
WHERE SEQ = #{seq}
AND DEL_YN = 'N'

mapper.delete:
/* MaUserDAO.deleteContents */
UPDATE T_USER
SET DEL_YN = 'Y'
  , MOD_DT = SYSDATE
  , MOD_ID = #{loginId}
WHERE SEQ = #{seq}
AND DEL_YN = 'N'
